implementation.constructor:
var img = document.getAnonymousNodes(this)[0];
var file = this.nodeValue();
if ( file.length == 0 ) {
  file = this.getAttributeNS("http://www.w3.org/1999/xlink","href");
}
// HACK: using img.src=file 'inline' doesn't seem to work
//       but it does when called from a setTimeout()
var f = function() { img.src = file; img.width = 500; }
setTimeout(f, 0);
